local allowedHWIDs = {
    -- Add HWIDs here if you want HWID lock:
    -- ["12345678-1234-1234-1234-123456789012"] = true,
}

local function getHWID()
    return game:GetService("RbxAnalyticsService"):GetClientId()
end

function SecurityLoader.ValidateHWID()
    if next(allowedHWIDs) == nil then
        return true, "No HWID restriction"
    end
    
    local hwid = getHWID()
    if allowedHWIDs[hwid] then
        return true, "HWID valid"
    else
        return false, "HWID not whitelisted: " .. hwid
    end
end

-- Load Module Function
function SecurityLoader.LoadModule(moduleName)
    -- Validate HWID
    local isAllowed, message = SecurityLoader.ValidateHWID()
    if not isAllowed then
        warn("üö´ Access Denied:", message)
        return nil
    end
    
    -- Get encrypted URL
    local encrypted = encryptedURLs[moduleName]
    if not encrypted then
        warn("‚ùå Module not found:", moduleName)
        return nil
    end
    
    -- Decrypt URL
    local url = decrypt(encrypted, SECRET_KEY)
    
    -- Load module
    local success, result = pcall(function()
        return loadstring(game:HttpGet(url))()
    end)
    
    if not success then
        warn("‚ùå Failed to load", moduleName, ":", result)
        return nil
    end
    
    return result
end

-- Anti-Dump Protection
function SecurityLoader.EnableAntiDump()
    local mt = getrawmetatable(game)
    if not mt then return end
    
    local oldNamecall = mt.__namecall
    setreadonly(mt, false)
    
    mt.__namecall = newcclosure(function(self, ...)
        local method = getnamecallmethod()
        
        if method == "HttpGet" or method == "GetObjects" then
            local caller = getcallingscript()
            if caller and caller ~= script then
                warn("üö´ Blocked dump attempt")
                return ""
            end
        end
        
        return oldNamecall(self, ...)
    end)
    
    setreadonly(mt, true)
    print("üõ°Ô∏è Anti-Dump Protection: ACTIVE")
end

